/** parse a match-syntax string into json */

var syntax = function syntax(input, opts, world) {
  // fail-fast
  if (input === null || input === undefined || input === '') {
    return [];
  }

  opts = opts || {};

  if (typeof input === 'number') {
    input = String(input); //go for it?
  }

  var tokens = parseBlocks(input); //turn them into objects

  tokens = tokens.map(function (str) {
    return parseToken(str, opts);
  }); // '~re-do~'

  tokens = splitHyphens(tokens, world); //clean up anything weird

  tokens = postProcess(tokens, opts); // console.log(tokens)

  return tokens;
};

